/**
 * Module dependencies.
 */
var util = require('util')
  , OAuth2Strategy = require('passport-oauth2')
  //, Profile = require('./profile')
  , InternalOAuthError = require('passport-oauth2').InternalOAuthError;

function Strategy(options, verify) {
  options = options || {};
  options.authorizationURL = options.authorizationURL || 'https://api.springrole.com/authorize';
  options.tokenURL = options.tokenURL || 'https://api.springrole.com/oauth/token';
  options.response_type='token';
  options.scope='science-hackathon';
  OAuth2Strategy.call(this, options, verify);
  this.name = 'springrole';
}

/**
 * Inherit from `OAuthStrategy`.
 */
util.inherits(Strategy, OAuth2Strategy);

/**
 * Expose `Strategy`.
 */
module.exports = Strategy;
